import styled from 'styled-components';
import { FaHeartBroken } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { Container } from '../../globalStyles';
import { Button } from '@mui/material';

export const SandboxPage = styled.div`
    min-height: 100vh;
    display: grid;
    place-items: center;
    background: hsl(323,21%,16%);
    color: pink;
    padding-right: 10rem;
`
export const NeonBtn = styled.a`
   &{
  color: ${props => props.NeonColor};
  font-size: 2rem;
  display: inline-block;
  cursor: pointer;
  border: currentColor 0.125em solid;
  padding: 0.25em 1.25em;
  border-radius: 0.25em;
  text-shadow: 0 0 0.125em hsl(0 0% 100% / 0.5), 0 0 0.25em currentColor;
  position: relative;
  box-shadow: inset 0 0 0.5em currentColor, 0 0 0.5em currentColor;
   }
  &:before {
    content: "";
    position: absolute;
    background: red;
    top: 120%;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    transform: perspective(1em) rotateX(40deg) translateZ(0em)
  }
`;
