import React, { useState, useEffect } from 'react';
import {
    HeaderButtons,
    NeonBtn,
    SandboxPage,
    ShowBtn
  } from './TestingStyles.jsx';
import "./Testing.scss"
function Testing() {
const [neonBtnClicked, setNeonBtnClicked] = useState(false)
const [neonBtnVisible, setNeonBtnVisible] = useState(false)
const colors = ["yellow","pink","blue","red","green"]
const [colorIndex,setColorIndex] = useState(0);
const [click, setClick] = useState(false);
// const [button, setButton] = useState(true);

const handleClick = () => setClick(!click);
const alternate = () => setNeonBtnVisible(!neonBtnVisible);


// const showButton = () => {
//     if (window.innerWidth <= 960) {
//     setButton(false);
//     } else {
//     setButton(true);
//     }
// };
const incrementColor = () => {
    const interval = setInterval(() => {
        colorIndex = colorIndex + 1
        if (colorIndex <= 5) {
            colorIndex = 0
        }
      }, 1000);
};


useEffect(() => {
    const interval = setInterval(() => {
    setColorIndex(prevIndex => prevIndex + 1)
    if (colorIndex >= 5) {
        setColorIndex(prevIndex => prevIndex - 4)
    }
    console.log(colors[colorIndex])
    }, 5000);
    return () => clearInterval(interval); // This represents the unmount function, in which you need to clear your interval to prevent memory leaks.
}, []);

// window.addEventListener('resize', showButton);

return (
    <>
    <HeaderButtons>
        <ShowBtn>
            Show a Neon Button
        </ShowBtn>
    </HeaderButtons>
    <SandboxPage>
        {neonBtnVisible ? (
            <>
            <NeonBtn NeonColor={colors[colorIndex]} onClick={() => setNeonBtnVisible(!neonBtnVisible)}>
                <span>Disco Time!</span>
            </NeonBtn>
            </>
        ) : (
        <NeonBtn NeonColor={colors[colorIndex]} onClick={() => setNeonBtnVisible(!neonBtnVisible)}>
            <span>Are you ready for some disco?</span>
        </NeonBtn>) }
    </SandboxPage>
    </>
);
}
export default Testing;
