import styled from 'styled-components';
import { FaHeartBroken } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { Container } from '../../globalStyles';
import { Button } from '@mui/material';
export const SandboxPage = styled.div`
    min-height: 100vh;
    display: grid;
    place-items: center;
    background: hsl(323,21%,16%);
    color: pink;

`
export const NeonBtn = styled.button`
  color: ${props => props.NeonColor};
  border-radius: 4px;
  background: ${({ primary }) => (primary ? 'rgb(0,0,0)' : '#0467FB')};
  white-space: nowrap;
  padding: ${({ big }) => (big ? '12px 64px' : '10px 20px')};
  font-size: ${({ fontBig }) => (fontBig ? '20px' : '16px')};
  outline: none;
  border: none;
  cursor: pointer;
  &:hover {
    transition: all 0.3s ease-out;
    background: #e9f362;
    background-color: ${({ primary }) => (primary ? 'rgb(93, 114, 0)' : 'rgb(0,0,0)')};
  }
  @media screen and (max-width: 960px) {
    width: 100%;
  }
`;
